css:
#wrapper{
    display: flex;
}
#modalWindowWrapper{
    position:fixed;
    display:flex;
    width:100%;
    height:100%;
    justify-content:center;
    align-items:center;
    background:rgba(0,0,0,.7);
    z-index:100;   
    top: 44px;  
    left: 0;
}
#modalWindow{
    position: fixed;
    top: 140px;
    margin-left: 50%;
    left: -450px;
    z-index: 100;
    background: #fafafa;
    width: 100%;
    max-width: 900px;
    padding: 15px;
}

#modalBtnWrapper{
    display: flex;
    justify-content: center;                        
}
#btnWrapper{
    display: flex;
    flex-direction: column;
    align-items: flex-end;                        
}
#renderBtn, #modalBtnTrue{
    background-color: #ff6e40;
}
#modalBtnExit{
    background-color: #ffffff;
    color: #333;
}
#modalBtnTrue, #modalBtnExit{
    margin: 8px 10px;
    box-shadow: 0 3px 1px -2px rgba(0,0,0,.2), 0 2px 2px 0 rgba(0,0,0,.14), 0 1px 5px 0 rgba(0,0,0,.12);                        
}
#modalTitle{
  font-weight: 600;
    text-align: center;
    font-size: 16px;
    margin: 10px 0;    
}
#contentWrapper{
    display: flex;
    justify-content: center;
}
.responseTest{
    font-size:  14px;
    margin: 5px 0;
}
.btn{
    border-radius: 4px;
    padding: 5px 20px;
    color: #fff;
    height: 28px;
    font-size: 16px;
    min-width: 60px!important;
    border: 0;
    font-weight: 600;
    outline: none;
