/*  start*/
/*   TRIANGLE  */
.orgLinksWrapper__triangle {
    width: 13px;
    height: 19px;
    border-bottom: solid 8px #e2e2e2;
    border-left: solid 8px transparent;
    border-right: solid 8px transparent;
    position: absolute;
    top: -18px;
    right: 4px;
}

.triangle, .arrived_triangle, .notCompetence_triangle, .overdue_triangle, .warning_triangle, .inWork_triangle, .toAttention_triangle, .onRefinement_triangle{
    width: 0;
    height: 0;
    position: absolute;
    border-bottom: 24px solid transparent;
    border-top: 24px solid transparent;
    border-left: 14px solid white;
    right: -14px;
    top: -1px;
}
.triangle:before, .arrived_triangle:before, .notCompetence_triangle:before,  .overdue_triangle:before, .warning_triangle:before, .inWork_triangle:before, .toAttention_triangle:before, .onRefinement_triangle:before{
    content: '';
    width: 0;
    height: 0;
    position: absolute;
    border-bottom: 20px solid transparent;
    border-top: 20px solid transparent;
    top: -20px;
    left: -14px;
}

.arrived_triangle:before{
    border-left: 12px solid rgb(74, 193, 197);
}
.notCompetence_triangle:before{
    border-left: 12px solid rgb(173, 118, 205);
}
.overdue_triangle:before{
    border-left: 12px solid rgb(240, 114, 93);
}
.warning_triangle:before{
    border-left: 12px solid rgb(238, 163, 54);
}
.inWork_triangle:before{
    border-left: 12px solid rgb(132, 199, 96);
}
.toAttention_triangle:before{
    border-left: 12px solid rgb(248, 195, 47);
}
.onRefinement_triangle:before{
    border-left: 12px solid rgb(94, 202, 162);
}
.triangle:before{
    border-left: 12px solid rgb(211, 211, 211);
}

/*  end*/

.displayNone{
