    .filterBox{
            padding: 5px 10px;
            border-width: 0px;
            max-width: 300px;
            height: 45px;
            background: inherit;
            background-color: rgba(239, 247, 249, 1);
            border: none;
            border-radius: 16px;
            -moz-box-shadow: none;
            -webkit-box-shadow: none;
            box-shadow: none;
            font-size: 14px;
            display: flex;
            justify-content: space-around;
            align-items: center;
            cursor: pointer;
            margin: 5px 0;
            font-weight: 800;        
    }

    .filterBox__value{
            margin-left: 20px;
            position: relative;
            display: inline-block;
            text-overflow: ellipsis;
            overflow: hidden;
            white-space: nowrap;
            max-width: 150px;  
            /*min-width: 60px;*/
            margin: 10px 0 10px 10px ;
    }
    [tooltip]:before {            
        position : absolute;
        content : attr(tooltip);
        opacity : 0;
    }
    [tooltip]:hover:before {        
        opacity : 1;
    }
    [tooltip]:not([tooltip-persistent]):before {
        pointer-events: none;
    }
    .filterBox__title{
        white-space: nowrap;
    }
