/* NEW STYLE FOR ELEMENTS HEADER */

.columnHeader{
    position: relative;
    height: 44px;
    border-top: 2px solid white;
    border-left: 2px solid white;
    border-bottom: 2px solid white;
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 0 10px;
    cursor: pointer;
    width: 100%; 
    color: #fff;
}
.b1{
    z-index: 100 ;
}
.b3{
    background: blue;
    left: -4px;
}
.b2{
    background: red;
    z-index: 90;
    left: -2px;
}
.triangle{
    width: 0;
    height: 0;
    position: absolute;
    border-bottom: 24px solid transparent;
    border-top: 24px solid transparent;
    border-left: 14px solid white;
    right: -14px;
    top: -4px;
}
.triangle4, .triangle5, .triangle6, .triangle2, .triangle3, .triangle7{
    width: 0;
    height: 0;
    position: absolute;
    border-bottom: 24px solid transparent;
    border-top: 24px solid transparent;
    border-left: 14px solid white;
    right: -14px;
    top: -4px;
}
.triangle4:before, .triangle5:before,  .triangle6:before, .triangle2:before, .triangle3:before, .triangle:before{
    content: '';
    width: 0;
    height: 0;
    position: absolute;
    border-bottom: 20px solid transparent;
    border-top: 20px solid transparent;
    top: -20px;
    left: -14px;
}

.triangle:before{
    border-left: 12px solid rgb(211, 211, 211);
}
.triangle2:before{
    border-left: 12px solid rgb(248, 195, 47);
}
.triangle3:before{
    border-left: 12px solid rgb(74, 193, 197);
}
.triangle4:before{
    border-left: 12px solid rgb(132, 199, 96);
}
.triangle5:before{
    border-left: 12px solid rgb(86 162 78);
}
.triangle6:before{
    border-left: 12px solid rgb(240, 114, 93);
}

/* header copy STYLES*/
    #container{
        margin: 30px auto 20px auto;                  
    }
    .tableContainer{
