.selectItem{
    background: transparent;
    border: none;
    border-bottom: 1px solid #acada8;
    margin: 10px 0;
    font-size: 14px;
    outline: none;
}
.select2{
    width: 100%!important;
    outline: none;                    
}
.select2-selection{
    border-radius: 0px!important;
    outline: none; 
    border: none!important;
    border-bottom: 1px solid #acada8!important;
}                
#assigmComment{
    width: 100%;
    padding: 5px;
    font-size: 16px;
    border: 0;
    margin: 7px 0;  
    outline: none;
}
#assigmRating{
    max-height: 100px;
}
#assigmResult, #assigmResolution{
    font-size: 16px;
    margin: 7px 0;
}
#assigmResolution, #assigmRating, #assigmComment{
    margin-top: 20px;
    border-bottom: 1px solid #acada8!important;
}
