#select2-resultSelect-container{
    padding-left: 20px!important;
}
#ratingElements{
    display: flex;
    margin: 10px 0px;                    
}
#buttonWrapper{
    display: flex;
    justify-content: center;
    width: 100%;                    
}
.modalBtn{
    text-transform: uppercase;
    outline: none;
    cursor: pointer;
    padding: 10px 15px;
    margin: 0 5px;                    
}
.displayNone{
    display: none;                    
}
#button_save{
    background-color: rgba(21, 189, 244, 1);
    border: none;
    border-radius: 5px;
    color: #fff;
}
#button_close{
    background-color: transparent;
    color: rgba(21, 189, 244, 1);
    border: none;
}     

.caption{
    font-size: 14px;
    text-transform: uppercase;
    margin: 0;
}

.container {
    display: block;
    position: relative;
    padding-left: 20px;
    cursor: pointer;
    font-size: 22px;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}

/* Hide the browser's default radio button */
.container input {
    cursor: pointer;
    height: 50px;
}
input.radio{
    width: 20px;
    height: 20px;
}    
/* Create a custom radio button */
.checkmark {
    position: absolute;
    top: 0;
    left: 0;
    height: 45px;
    width: 45px;
    background-color: #eee;
    border-radius: 50%;
}

/* On mouse-over, add a grey background color */
.container:hover input ~ .checkmark {
    background-color: #ccc;
}

/* When the radio button is checked, add a blue background */
.container input:checked ~ .checkmark {
    background-color: #2196F3;
}

/* Create the indicator (the dot/circle - hidden when not checked) */
.checkmark:after {
    content: "";
    position: absolute;
    display: none;
}

/* Show the indicator (dot/circle) when checked */
.container input:checked ~ .checkmark:after {
    display: block;
}

/* Style the indicator (dot/circle) */
.container .checkmark:after {
    top: 9px;
    left: 9px;
    width: 8px;
    height: 8px;
    border-radius: 50%;
    background: white;
}


.registrated{
    color: #fff;
}
.notDone{
    color: red;  
}
 .yellow{
    color: yellow; 
}
.inWork{
    color: green;
}
.onCheck{
    color: blue;
}
.closed{
    color: grey;
}
